/**
 * Part 1
 * ==========
 *
 * Given the instructions above, determine what floor santa ends up on.
 *
 * Part 2
 * ==========
 *
 * Given the same instructions, find the position of the first character that causes Santa to enter the basement.
 */

$currentFloor = 0;
$basementPosition = 0;

foreach (str_split($instructions) as $position => $instruction) {
    $instruction === '(' ? $currentFloor++ : $currentFloor--;

    if ($currentFloor === -1 && $basementPosition === 0) {
        $basementPosition = $position + 1;
    }
}

echo "Part 1 answer: {$currentFloor}\n";
echo "Part 2 answer: {$basementPosition}";

